pragma solidity ^0.4.24;

import "./SafeMath.sol";

contract Merchant {
    using SafeMath for uint256;

    uint256 public contractID;

    enum HolderStatus { Unverified, Verified, Inactive, Blacklisted }
    enum Roles { NoRole, Admin, Auditor, FundManager, Custodian, Trustee, Affiliate, Merchant }
    enum Level { CC, BB, AA }   //change to merchantLevel (0-100%) :: mapping(address => uint) public merchantLevel;
    uint[] public remittance;

    struct Merchants {
        string mName;
        address mAddress;
        uint mLevel;
        uint directSales;
        uint creditSales;
        uint vIssued;
        uint vMinBuy;
        uint vSold;
        uint vRemitted;
        uint vRedeemed;
        uint vDiscount;   // 500 for 5%
        uint vTenor;       // in days        
    }
    Merchants[] public merchants;  //TO-DO: change to private

    struct PaymentRequests {
        uint merchantID;
        uint amount;
        bool paid;
    }
    mapping (uint => PaymentRequests[]) public request;
    mapping (address => uint) public merchantID;

    uint totalMerchants;
    mapping (address => uint) public customers; 
    uint totalCustomers = 1;


    mapping(uint => mapping(uint => uint)) public pdBalance;  //pdBalance[customeID][MerchantID]
    mapping(uint => mapping(uint => uint)) public rtBalance;  //rtBalance[customerID][MerchantID]
    uint public minAmount = 10000;
   // uint public minDiscount;
    uint public fee = 100; //1%
    uint public feeDenominator = 10000; //100%
    //uint public totalNoOfOffers;

    event SetTokenAndRegistry(address indexed token, address indexed registry, uint256 contractID);
    event VoucherBought(address indexed buyer, uint indexed merchantID, uint amount);
    event VoucherRedeemed(address indexed buyer, uint indexed merchantID, uint amount);
    event VoucherShared(uint256 indexed merchantID, uint indexed senderID, uint receiverID, uint amount);
    event RequestRedemption(uint256 indexed merchantID, uint customerID, uint amount);

	mapping (address => uint) balances;

	event Transfer(address indexed _from, address indexed _to, uint256 _value);

    constructor() public {
		balances[msg.sender] = 10000000;
        remittance = [9000, 7500, 5000];
        customers[0x7dd147674E79Df3f3b88c2BDfb1E4AF9Bc73C57A] = 1;
        customers[0x04Ec558aa3205a74673902Ae754c87fc8D8cbed2] = 2;
        totalCustomers = 3;

        merchants.push(Merchants({
            mName: "Ego Owner",
            mAddress: 0x142e98a282aEa6ceEE893F4c1ad256068b1cd31E,
            mLevel: 0,
            directSales: 0,
            creditSales: 0,
            vIssued: 0,
            vMinBuy: 0,
            vSold: 0,
            vRemitted: 0,
            vRedeemed: 0,
            vDiscount: 0,
            vTenor: 0
        }));

        merchants.push(Merchants({
            mName: "Snal Snail Shop",
            mAddress: 0xaC515d20Ac2FDc7434cCe96AA5437e18d10762E5,
            mLevel: 1,
            directSales: 0,
            creditSales: 0,
            vIssued: 10000000,
            vMinBuy: 10000,
            vSold: 0,
            vRemitted: 0,
            vRedeemed: 0,
            vDiscount: 500,
            vTenor: 0
        }));

        merchants.push(Merchants({
            mName: "Farm Store Snail Shop",
            mAddress: 0x6d28E4BE25C5a12ba395C3a8AF84d7119562c743,
            mLevel: 2,
            directSales: 0,
            creditSales: 0,
            vIssued: 2000000,
            vMinBuy: 2000,
            vSold: 0,
            vRemitted: 0,
            vRedeemed: 0,
            vDiscount: 700,
            vTenor: 0
        }));
    }

	function sendCoin(address receiver, uint amount) public returns(bool sufficient) {
		if (balances[msg.sender] < amount) return false;
		balances[msg.sender] -= amount;
		balances[receiver] += amount;
		emit Transfer(msg.sender, receiver, amount);
		return true;
	}

	function getBalance(address addr) public view returns(uint) {
		return balances[addr];
	}

	function getCustomerStatus(address addr) public view returns(bool) {
		if (customers[addr] == 0) return false;
        return true;
	}

    function addNewAddress(address addr) public returns(bool) {
        require(customers[msg.sender] > 0, "You are not authorised");
        require(customers[addr] == 0, "addr already registered");
		customers[addr] = customers[msg.sender];
        return true;
	}

    function setMinimums(uint _minAmount) public {
        minAmount = _minAmount;
    }

    function setRemittance(uint[] _remittance) public {
        require(_remittance.length == 3, "_remittance lenght must be 3");
        remittance = _remittance;
    }

    function newMarchant(string _mName, address _owner, uint _level) public returns (bool) {
        require(_level < 3, "_level either 0, 1, or 2");
       // Merchants storage m = merchants[totalMerchants];
        //TO-DO: add _owner to registry, set _owenr as registered
        require(merchantID[_owner] == 0, "merchant previously added");
        merchantID[_owner] = merchants.length;
        merchants.push(Merchants({
            mName: _mName,
            mAddress: _owner,
            mLevel: _level,
            directSales: 0,
            creditSales: 0,
            vIssued: 0,
            vMinBuy: 0,
            vSold: 0,
            vRemitted: 0,
            vRedeemed: 0,
            vDiscount: 0,
            vTenor: 0
        }));
        
        return true;
    }

    function changeMerchantAddress(uint _merchantID, address _newAddress) public returns (bool) {
        Merchants storage m = merchants[_merchantID];
        //TO-DO: add _owner to registry
        require(m.mAddress == msg.sender, "you are not the original merchant");
        m.mAddress = _newAddress;
        return true;
    }   

    function newCustomer() public returns (bool) {
        require(customers[msg.sender] == 0, "customer previously registered");
        customers[msg.sender] = totalCustomers;
        totalCustomers += 1;
        return true;
    }    

    function changeCustomerAddress(address _newAddress) public returns (bool) {
        require(customers[msg.sender] != 0, "customer not previously registered");
        customers[_newAddress] = customers[msg.sender];
        customers[msg.sender] = 0;
        return true;
    }    

    function createOffers(uint _merchantID, uint _amountIssued, uint _minBuy, uint _discount, uint _tenorDays) public returns (bool){
        require(_amountIssued >= minAmount, "_amountIssued cannot be lower than minAmount");
        require(_discount >= fee, "_discount cannot be lower than fee");  //javascript
        require(_amountIssued >= _minBuy.mul(10), "_minBuy cannot be higher than 10% of amountIssued");
        Merchants storage m = merchants[_merchantID];
        require(m.mAddress == msg.sender, "wrong _merchantID");   //TO-DO: use modifier
        require(m.vIssued == 0, "Offer previously created");
        m.vIssued = _amountIssued;
        m.vMinBuy = _minBuy;
        m.vDiscount = _discount.sub(fee);
        m.vTenor = block.timestamp + _tenorDays * 1 days;
        //totalNoOfOffers += 1;
        return true;
    }

    function reviseOffers(uint _merchantID, uint _rAmountIssued, uint _rMinBuy, uint _rDiscount, uint _rTenorDays) public {
        Merchants storage m = merchants[_merchantID];
        require(_rAmountIssued >= minAmount, "_amountIssued cannot be lower than minAmount/m.vIssued");
        require(_rAmountIssued >= m.vSold, "_amountIssued cannot be lower than already sold voucher");
        require(_rDiscount >= fee, "_discount cannot be lower than fee");
        require(_rAmountIssued >= _rMinBuy.mul(10), "_minBuy cannot be higher than 10% of amountIssued");
        require(m.mAddress == msg.sender, "wrong _merchantID");   //TO-DO: use modifier
        m.vIssued = _rAmountIssued;
        m.vMinBuy = _rMinBuy;
        m.vDiscount = _rDiscount.sub(fee);
        m.vTenor = block.timestamp + _rTenorDays * 1 days;
        //totalNoOfOffers += 1;
    }

    //to buy or topUp voucher
    function buyVoucher(uint _merchantID, uint _amount) public returns (bool) {
        Merchants storage m = merchants[_merchantID];
        uint _customerID = customers[msg.sender]; 
        uint amount = _amount;
        uint price = amount.sub(amount.mul(m.vDiscount).div(feeDenominator));
        uint platformFee = amount.mul(fee).div(feeDenominator);
        uint receivable = price.sub(platformFee);
        uint retained = receivable.mul(feeDenominator.sub(remittance[uint(m.mLevel)])).div(feeDenominator);
        uint pdBought = amount.sub(retained);
        pdBalance[_customerID][_merchantID] = pdBalance[_customerID][_merchantID].add(pdBought);
        rtBalance[_customerID][_merchantID] = rtBalance[_customerID][_merchantID].add(retained);
        m.vSold = m.vSold.add(amount);
        m.vRemitted = m.vRemitted.add(amount).sub(retained);
        sendCoin(m.mAddress, price);
        emit VoucherBought(msg.sender, _merchantID, _amount); //TO-Do: change to _merchantID, uint _offerID
        return true;
    }

    //to be used for online purchases
    function redeemVoucherOnline(uint _merchantID, uint _amount) public returns (bool) {
        require(_merchantID < merchants.length, "_merchantID does not exist"); 
        Merchants storage m = merchants[_merchantID];
        uint _cID = customers[msg.sender]; 
        require(pdBalance[_cID][_merchantID].add(rtBalance[_cID][_merchantID]) >= _amount, "insufficient voucher balance");
        m.vRedeemed = m.vRedeemed.add(_amount);
        if (pdBalance[_cID][_merchantID] >= _amount) {
            pdBalance[_cID][_merchantID] = pdBalance[_cID][_merchantID].sub(_amount);
        } else {
            uint remain = _amount.sub(pdBalance[_cID][_merchantID]);
            pdBalance[_cID][_merchantID] = 0;
            rtBalance[_cID][_merchantID] = rtBalance[_cID][_merchantID].sub(remain);
            m.vRemitted = m.vRemitted.add(remain);
            sendCoin(m.mAddress, remain);
        }
        emit VoucherRedeemed(msg.sender, _merchantID, _amount);
        return true;
    }

    //to be used in offline Stores
    //To-Do: change modidifer to merchantAgent, add role
    function requestRedemption(uint _merchantID, uint _customerID, uint _amount) public returns (bool) {
        require(_merchantID < totalMerchants, "_merchantID does not exist"); 
        require(_customerID <= totalCustomers, "_customerID does not exist"); 
        request[_customerID].push(PaymentRequests({
            merchantID: _merchantID,
            amount: _amount,
            paid: false
        }));
        emit RequestRedemption(_merchantID, _customerID, _amount);
        return true;
    }
    //to be used for Instore purchases
    //To-Do: change modidifer to merchantAgent, add role
    function redeemVoucherInstore(uint _requestID) public returns (bool) {
        uint _cID = customers[msg.sender]; 
        PaymentRequests storage r = request[_cID][_requestID];
        Merchants storage m = merchants[r.merchantID];
        require(!r.paid, "request previously approved");
        r.paid = true;
        require(pdBalance[_cID][r.merchantID].add(rtBalance[_cID][r.merchantID]) >= r.amount, "insufficient voucher balance");
        m.vRedeemed = m.vRedeemed.add(r.amount);
        if (pdBalance[_cID][r.merchantID] >= r.amount) {
            pdBalance[_cID][r.merchantID] = pdBalance[_cID][r.merchantID].sub(r.amount);
        } else {
            uint remain = r.amount.sub(pdBalance[_cID][r.merchantID]);
            pdBalance[_cID][r.merchantID] = 0;
            rtBalance[_cID][r.merchantID] = rtBalance[_cID][r.merchantID].sub(remain);
            m.vRemitted = m.vRemitted.add(remain);
            sendCoin(m.mAddress, remain);
        }
        emit VoucherRedeemed(msg.sender, r.merchantID, r.amount);
        return true;
    }

    //to share voucher with another customer
    function shareVoucher(uint _merchantID, uint _receiverID, uint _amount) public returns (bool) {
        uint _cID = customers[msg.sender]; 
        require(pdBalance[_cID][_merchantID] >= _amount, "insufficient credit");
        pdBalance[_cID][_merchantID] = pdBalance[_cID][_merchantID].sub(_amount);  //TO-DO: pay fee
        pdBalance[_receiverID][_merchantID] = pdBalance[_receiverID][_merchantID].add(_amount); 
        emit VoucherShared(_merchantID, _cID, _receiverID, _amount);
        return true;
    }

    //TO_DO: Add to main contract
    function shareVoucherWithAddress(uint _merchantID, address receiverAddr, uint _amount) public returns (bool) {
        uint _cID = customers[msg.sender]; 
        uint _rID = customers[receiverAddr]; 
        require(pdBalance[_cID][_merchantID] >= _amount, "insufficient credit");
        pdBalance[_cID][_merchantID] = pdBalance[_cID][_merchantID].sub(_amount);  //TO-DO: pay fee
        pdBalance[_rID][_merchantID] = pdBalance[_rID][_merchantID].add(_amount); 
        emit VoucherShared(_merchantID, _cID, _rID, _amount);
        return true;
    }

    function getVoucherDetailedBalances(uint _merchantID) public view returns (uint, uint, uint) {
        uint _cID = customers[msg.sender]; 
        uint pd = pdBalance[_cID][_merchantID];
        uint rt = rtBalance[_cID][_merchantID];
        uint total = pd.add(rt);
        return (pd, rt, total);
    }    

    function getVoucherBalances(uint _merchantID) public view returns (uint) {
        uint _cID = customers[msg.sender]; 
        uint pd = pdBalance[_cID][_merchantID];
        uint rt = rtBalance[_cID][_merchantID];
        uint total = pd.add(rt);
        return total;
    }

    function getCustomerId() public view returns (uint) {
        return customers[msg.sender]; 
    }

}
